import React, { useEffect, useRef } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import {
    faFacebook,
    faGithub,
    faLinkedin,
    faMedium,
    faStackOverflow,
} from "@fortawesome/free-brands-svg-icons";
import { Box, HStack } from "@chakra-ui/react";

const socials = [{
        icon: faFacebook,
        url: "https://facebook.com",
    },
    {
        icon: faGithub,
        url: "https://github.com",
    },
    {
        icon: faLinkedin,
        url: "https://www.linkedin.com",
    },
    {
        icon: faMedium,
        url: "https://medium.com",
    },
    {
        icon: faStackOverflow,
        url: "https://stackoverflow.com",
    },
];

const Header = () => {

    const [prevScrollPos, setPrevScrollPos] = useState(0);
    const [visible, setVisible] = useState(true);
    const headerRef = useRef(null);
    useEffect(() => {
        const handleScroll = () => {
            const currentScrollPos = window.pageYOffset;

            setVisible(prevScrollPos > currentScrollPos || currentScrollPos < 200);

            setPrevScrollPos(currentScrollPos);
        };

        window.addEventListener("scroll", handleScroll);
        return () => {
            window.removeEventListener("scroll", handleScroll);
        };
    }, [prevScrollPos]);

    const handleClick = (anchor) => () => {
        const id = `${anchor}-section`;
        const element = document.getElementById(id);
        if (element) {
            element.scrollIntoView({
                behavior: "smooth",
                block: "start",
            });
        }
    };

    return ( <
        Box position = "fixed"
        top = { 0 }
        left = { 0 }
        right = { 0 }
        translateY = { 0 }
        transitionProperty = "transform"
        transitionDuration = ".3s"
        transitionTimingFunction = "ease-in-out"
        backgroundColor = "#18181b" >
        <
        Box color = "white"
        maxWidth = "1280px"
        margin = "0 auto" >
        <
        HStack px = { 16 }
        py = { 4 }
        justifyContent = "space-between"
        alignItems = "center" >
        <
        nav > { /* Add social media links based on the `socials` data */ } <
        HStack spacing = { 8 } > {
            socials.map((social, index) => ( <
                a key = { index }
                href = { social.url }
                target = "_blank"
                rel = "noopener noreferrer" >
                <
                FontAwesomeIcon icon = { social.icon }
                size = "2x" / >
                <
                /a>
            ))
        } <
        /HStack> <
        /nav> <
        nav >
        <
        HStack spacing = { 8 } > { /* Add links to Projects and Contact me section */ } <
        a href = "/#projects-section"
        onClick = {
            (event) => handleClick(event, 'projects-section') } >
        Projects <
        /a> <
        a href = "/#contactme-section"
        onClick = {
            (event) => handleClick(event, 'contactme-section') } >
        Contact Me <
        /a> <
        /HStack> <
        /nav> <
        /HStack> <
        /Box> <
        /Box>
    );
};
export default Header;
