import { Heading, HStack, Image, Text, VStack, Box, Link } from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";

const Card = ({ title, description, imageSrc, link }) => {
  // Implement the UI for the Card component according to the instructions.
  // You should be able to implement the component with the elements imported above.
  // Feel free to import other UI components from Chakra UI if you wish to.

  return (
    <Box
    borderWidth="1px"
    borderRadius="lg"
    overflow="hidden"
    padding="16px"
    _hover={{ shadow: "md" }}
    display="flex"
    flexDirection="column"
    alignItems="center"
  >
    <Image src={imageSrc} alt={`${title} image`} boxSize="150px" objectFit="cover" borderRadius="md" marginBottom="16px" />

    <VStack spacing="8px" align="start" width="100%">
      <Heading size="md">{title}</Heading>
      <Text>{description}</Text>
      <Link href={link} isExternal color="lightblue">
        <HStack spacing="4px">
          <Text>See more</Text>
          <FontAwesomeIcon icon={faArrowRight} size="1x" />
        </HStack>
      </Link>
    </VStack>
  </Box>
  )
};

export default Card;
